import React from 'react';
import { Nav, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import '../css/register.css'
import { RegisterMe } from '../services/RegisterMe';
import loader from '../images/loader.gif'
class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isloading: false,
            successMessage: false
        };
    }
    handleChange = async (e) => {
        await this.setState({[e.target.name]: e.target.value})
        console.log("current data", this.state)
    }
    formSubmit = (e) => {
        e.preventDefault();
        this.setState({isloading: true})
        var data = {
            handle: this.state.username,
            email: this.state.email,
            password: this.state.password,
            confirmPassword: this.state.password
        }
        RegisterMe(data).then((res)=> {
            console.log("sucess", res)
            this.setState({isloading: false})
            if(res.status === 201) {
                this.setState({successMessage: "Registration Successful"})
            }
        }).catch((err) => {
            console.log("error", err)
        })

    }
    render() {
        return (

            <div>
                {!this.state.isloading ? (<div className="loader"><img src = {loader}/></div>) : ''}
                <div className="rsection signup">
                    <div className="container">
                        <div className="row itemcenter">
                            <div className="col-sm-6 center ">
                                <div className="welcome">
                                    <h4 className="sectionwel">WELCOME TO <br />DESIGNMOCHA.</h4>
                                </div>
                                <Link to="/" className="btn my-btn">Back To Home </Link>
                            </div>
                            <div className="col-sm-6 form">
                                <div className="rflex">
                                    <form onSubmit={this.formSubmit}>
                                        <p className="text-color">{!this.state.successMessage ? '': this.state.successMessage}{this.state.isloading ? "Loading please wait..." : ''}</p>
                                        <div className="form-group">
                                            <label for="exampleInputEmail1" className="fotmlab">FULL NAME</label>
                                            <input type="text" name="username" className="form-control" id="exampleInputEmail1" aria-describedby="FullName" placeholder="Sahil Mahajan" required onChange={this.handleChange}/>
                                        </div>
                                        <div className="form-group">
                                            <label for="exampleInputEmail1" className="fotmlab"> EMAIL ADDRESS</label>
                                            <input type="email" name="email" className="form-control" id="exampleInputEmail1" aria-describedby="Email" placeholder="Sahil@breathingcrafts.com" required onChange={this.handleChange}/>
                                        </div>
                                        <div className="form-group">
                                            <label for="exampleInputPassword1" className="fotmlab">PASSWORD</label>
                                            <input type="password" name="password" className="form-control" id="exampleInputPassword1" placeholder="*********" required onChange={this.handleChange}/>
                                        </div><br />
                                        <Form.Check
                                            type="switch"
                                            id="custom-switch"
                                            label="PRIVACY POLICY, LEGAL POLICY, TERMS OF SERVICE"
                                            className="text-color"
                                            required
                                        />
                                        <button type="submit" className="btn form">SIGN ME UP</button>
                                        <Link to="/login" className="btn form a">I am already a member</Link>
                                    </form>
                                    <span className="flow"> or connect with -
                                        <Nav.Link href="/"><i className="cl fa fa-facebook" aria-hidden="true"></i></Nav.Link>
                                        <Nav.Link href="/"><i className="cl fa fa-instagram" aria-hidden="true"></i></Nav.Link>
                                        <Nav.Link href="/"><i className="cl fa fa-twitter" aria-hidden="true"></i></Nav.Link>
                                        <Nav.Link href="/"><i className="cl fa fa-pinterest-p" aria-hidden="true"></i></Nav.Link>
                                    </span>
                                </div>
                            </div>

                        </div>

                    </div>

                </div>
            </div>
        );
    }
}

Register.propTypes = {};

export default Register;
