import axios from "axios"
export const uploadAvatar = (file) => {
    return new Promise((resolve, reject) => {
        var data = new FormData()
        data.append('pic', file)
        axios.patch("https://designmocha-dev.el.r.appspot.com/public/profile/update-profile-pic", data)
        .then((res) => {
            resolve(res)
        })
        .catch((error) => {
            reject(error)
        })
    }) 
}
